import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import UserService from "../service/UserService";
import { FaUser, FaLock } from "react-icons/fa";
import { FcGoogle } from "react-icons/fc";
import { FaFacebookF } from "react-icons/fa";

function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userData = await UserService.login(email, password);
      if (userData.token) {
        localStorage.setItem("token", userData.token);
        localStorage.setItem("role", userData.role);
        navigate("/profile");
      } else {
        setError(userData.message);
      }
    } catch (error) {
      setError("Đăng nhập thất bại. Vui lòng thử lại!");
      setTimeout(() => setError(""), 5000);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-gray-300 to-gray-600">
      <div className="bg-white shadow-lg rounded-2xl p-8 w-96 text-center">
        <div className="flex justify-center mb-4">
          <img
            src="https://cdn-icons-png.flaticon.com/128/742/742751.png"
            alt="Avatar"
            className="w-16 h-16"
          />
        </div>
        <form onSubmit={handleSubmit}>
          {error && <p className="text-red-500 mb-3">{error}</p>}
          <div className="relative mb-4">
            <FaUser className="absolute left-3 top-3 text-gray-400" />
            <input
              type="email"
              placeholder="Tên đăng nhập"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full px-10 py-2 border rounded-md focus:outline-none"
            />
          </div>
          <div className="relative mb-4">
            <FaLock className="absolute left-3 top-3 text-gray-400" />
            <input
              type="password"
              placeholder="Mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-10 py-2 border rounded-md focus:outline-none"
            />
          </div>
          <div className="flex space-x-2">
            <button
              type="submit"
              className="w-1/2 bg-purple-500 text-white py-2 rounded-md hover:bg-purple-700"
            >
              Đăng nhập
            </button>
            <button
              type="button"
              className="w-1/2 bg-blue-500 text-white py-2 rounded-md hover:bg-blue-700"
            >
              Đăng ký
            </button>
          </div>
        </form>
        <p className="text-gray-500 text-sm mt-4">Quên mật khẩu?</p>
        <div className="flex items-center justify-center space-x-4 mt-4">
          <FaFacebookF className="text-blue-600 text-2xl cursor-pointer" />
          <span className="text-gray-500">or</span>
          <FcGoogle className="text-2xl cursor-pointer" />
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
